const ContactUs = ({ theme }) => {
    const [formData, setFormData] = useState({
        name: "",
        email: "",
        message: "",
    });
    const [isSubmitted, setIsSubmitted] = useState(false);
    const [isLoading, setIsLoading] = useState(false);
    const nameInputRef = useRef(null);

    useEffect(() => {
        nameInputRef.current.focus();
    }, []);

    const handleSubmit = (e) => {
        e.preventDefault();
        if (formData.name && formData.email && formData.message) {
            setIsLoading(true);

            setTimeout(() => {
                setIsLoading(false);
                setIsSubmitted(true);
                setFormData({ name: "", email: "", message: "" });
            }, 1500);
        } else {
            alert("Будь ласка, заповніть всі поля.");
        }
    };

    return (
        <div
            style={{
                backgroundColor: theme === "light" ? "#fff" : "#333",
                color: theme === "light" ? "#000" : "#fff",
                minHeight: "calc(100vh - 200px)",
                padding: "2rem 0",
            }}
        >
            <div style={{ maxWidth: "800px", margin: "0 auto", padding: "0 2rem" }}>
                <h1 style={{
                    fontSize: "2.5rem",
                    marginBottom: "2rem",
                    color: theme === "light" ? "#0057b7" : "#ffd700",
                    textAlign: "center"
                }}>
                    Зворотний зв'язок
                </h1>

                {isSubmitted ? (
                    <div style={{
                        backgroundColor: theme === "light" ? "#e6f7e6" : "#2a3a2a",
                        padding: "2rem",
                        borderRadius: "8px",
                        textAlign: "center",
                        boxShadow: "0 4px 8px rgba(0,0,0,0.1)"
                    }}>
                        <h2 style={{ color: theme === "light" ? "#0057b7" : "#ffd700", marginBottom: "1rem" }}>
                            Дякуємо за ваше звернення!
                        </h2>
                        <p style={{ marginBottom: "1.5rem" }}>
                            Ми отримали ваше повідомлення та зв'яжемося з вами найближчим часом.
                        </p>
                        <button
                            onClick={() => setIsSubmitted(false)}
                            style={{
                                backgroundColor: theme === "light" ? "#0057b7" : "#ffd700",
                                color: theme === "light" ? "#fff" : "#000",
                                border: "none",
                                padding: "0.8rem 1.5rem",
                                borderRadius: "4px",
                                cursor: "pointer",
                                fontWeight: "bold",
                                transition: "all 0.3s",
                                ":hover": {
                                    backgroundColor: theme === "light" ? "#004494" : "#e6c200"
                                }
                            }}
                        >
                            Написати ще
                        </button>
                    </div>
                ) : (
                    <form
                        onSubmit={handleSubmit}
                        style={{
                            backgroundColor: theme === "light" ? "#f8f9fa" : "#2a2a2a",
                            padding: "2rem",
                            borderRadius: "8px",
                            boxShadow: "0 4px 8px rgba(0,0,0,0.1)"
                        }}
                    >
                        <div style={{ marginBottom: "1.5rem" }}>
                            <label
                                htmlFor="name"
                                style={{
                                    display: "block",
                                    marginBottom: "0.5rem",
                                    fontWeight: "bold",
                                    color: theme === "light" ? "#0057b7" : "#ffd700"
                                }}
                            >
                                Ім'я
                            </label>
                            <input
                                type="text"
                                id="name"
                                placeholder="Ваше ім'я"
                                value={formData.name}
                                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                                ref={nameInputRef}
                                style={{
                                    width: "100%",
                                    padding: "0.8rem",
                                    borderRadius: "4px",
                                    border: `1px solid ${theme === "light" ? "#ddd" : "#555"}`,
                                    backgroundColor: theme === "light" ? "#fff" : "#3a3a3a",
                                    color: theme === "light" ? "#000" : "#fff"
                                }}
                                required
                            />
                        </div>

                        <div style={{ marginBottom: "1.5rem" }}>
                            <label
                                htmlFor="email"
                                style={{
                                    display: "block",
                                    marginBottom: "0.5rem",
                                    fontWeight: "bold",
                                    color: theme === "light" ? "#0057b7" : "#ffd700"
                                }}
                            >
                                Email
                            </label>
                            <input
                                type="email"
                                id="email"
                                placeholder="Ваш email"
                                value={formData.email}
                                onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                                style={{
                                    width: "100%",
                                    padding: "0.8rem",
                                    borderRadius: "4px",
                                    border: `1px solid ${theme === "light" ? "#ddd" : "#555"}`,
                                    backgroundColor: theme === "light" ? "#fff" : "#3a3a3a",
                                    color: theme === "light" ? "#000" : "#fff"
                                }}
                                required
                            />
                        </div>

                        <div style={{ marginBottom: "2rem" }}>
                            <label
                                htmlFor="message"
                                style={{
                                    display: "block",
                                    marginBottom: "0.5rem",
                                    fontWeight: "bold",
                                    color: theme === "light" ? "#0057b7" : "#ffd700"
                                }}
                            >
                                Повідомлення
                            </label>
                            <textarea
                                id="message"
                                placeholder="Ваше повідомлення"
                                value={formData.message}
                                onChange={(e) => setFormData({ ...formData, message: e.target.value })}
                                rows="5"
                                style={{
                                    width: "100%",
                                    padding: "0.8rem",
                                    borderRadius: "4px",
                                    border: `1px solid ${theme === "light" ? "#ddd" : "#555"}`,
                                    backgroundColor: theme === "light" ? "#fff" : "#3a3a3a",
                                    color: theme === "light" ? "#000" : "#fff",
                                    resize: "vertical"
                                }}
                                required
                            ></textarea>
                        </div>

                        <button
                            type="submit"
                            disabled={isLoading}
                            style={{
                                width: "100%",
                                backgroundColor: isLoading
                                    ? theme === "light" ? "#7a9cc6" : "#b3a300"
                                    : theme === "light" ? "#0057b7" : "#ffd700",
                                color: theme === "light" ? "#fff" : "#000",
                                border: "none",
                                padding: "1rem",
                                borderRadius: "4px",
                                cursor: "pointer",
                                fontWeight: "bold",
                                fontSize: "1.1rem",
                                transition: "all 0.3s",
                                ":hover": {
                                    backgroundColor: isLoading
                                        ? theme === "light" ? "#7a9cc6" : "#b3a300"
                                        : theme === "light" ? "#004494" : "#e6c200"
                                }
                            }}
                        >
                            {isLoading ? (
                                <>
                  <span style={{
                      display: "inline-block",
                      width: "16px",
                      height: "16px",
                      border: `2px solid ${theme === "light" ? "#fff" : "#000"}`,
                      borderTopColor: "transparent",
                      borderRadius: "50%",
                      marginRight: "8px",
                      animation: "spin 1s linear infinite",
                      verticalAlign: "middle"
                  }}></span>
                                    Відправка...
                                </>
                            ) : "Надіслати"}
                        </button>
                    </form>
                )}

                <div style={{
                    marginTop: "3rem",
                    backgroundColor: theme === "light" ? "#f8f9fa" : "#2a2a2a",
                    padding: "2rem",
                    borderRadius: "8px",
                    boxShadow: "0 4px 8px rgba(0,0,0,0.1)"
                }}>
                    <h2 style={{
                        color: theme === "light" ? "#0057b7" : "#ffd700",
                        marginBottom: "1.5rem",
                        textAlign: "center"
                    }}>
                        Наші контакти(їх нема...)
                    </h2>

                </div>
            </div>
        </div>
    );
};

export default function App() {
    const [theme, setTheme] = useState("light");

    const toggleTheme = () => {
        setTheme(theme === "light" ? "dark" : "light");
    };

    return (
        <Router>
            <div
                className="App"
                style={{
                    backgroundColor: theme === "light" ? "#fff" : "#333",
                    color: theme === "light" ? "#000" : "#fff",
                    minHeight: "100vh",
                    display: "flex",
                    flexDirection: "column"
                }}
            >
                <Header theme={theme} toggleTheme={toggleTheme} />
                <main style={{ flex: "1" }}>
                    <Routes>
                        <Route path="/" element={<Home theme={theme} />} />
                        <Route path="/about" element={<AboutUkraine theme={theme} />} />
                        <Route path="/cities" element={<Cities theme={theme} />} />
                        <Route path="/news" element={<News theme={theme} />} />
                        <Route path="/gallery" element={<Gallery theme={theme} />} />
                        <Route path="/contact" element={<ContactUs theme={theme} />} />
                    </Routes>
                </main>
                <Footer theme={theme} />
            </div>
        </Router>
    );
}
